mapper.select:
select appartment.apartment_id appartment_id,appartment.apartment_name appartment_name,price,address,name,type_name, picture.pic_id,pic_addr,pic_name from appartment,appartment_type,account,apartment_pic,picture where appartment.type_id=appartment_type.type_id and appartment.owner=account.account_id and appartment.apartment_id=apartment_pic.apartment_id and apartment_pic.pic_id=picture.pic_id and appartment.status=0
and apartment_name like concat('%',#{appartmentVO.appartmentName},'%')
and address like concat('%',#{appartmentVO.address},'%')
and price between #{appartmentVO.beginPrice} and #{appartmentVO.endPrice}
and price >= #{appartmentVO.beginPrice}
and price <= #{appartmentVO.endPrice}
